import {
  BrowserRouter,
  Routes,
  Route,
} from "react-router-dom";
import Home from './Pages/home/Home.jsx';
import Introduction from "./Pages/introduction/Introduction.jsx";
import axios from 'axios'
function App() {

    //handler for the addModule button
    function submit(e) {
        e.preventDefault();
        let code = document.getElementById("input").value;

        let data = { code:code };
        axios.post('http://localhost:3001/introduction',data)

        .then (res => res.text())
        .then (txt => alert(txt))
    }

    //when the page loads
    window.onload = () => { 
        document.getElementById("button").onclick = submit;
    }


  return (
    <div className="App">
                      <div className="widgets">
                    <h3 >Exercise: </h3>

                        <p>Please try to output this "Hello A Java Tutor!" by using the basic Syntax (System.out.println())</p>

                        	<div>
                                <label>please enter your Java code here:</label> 
                                <input type="text" id = "input"/>
                            </div>
	                        <button id="button">submit</button>

                 </div>
          <BrowserRouter>
          <Routes>

              <Route path="/" element={<Home/>}/>

              <Route path='introduction' element={<Introduction />}/>







          </Routes>
        </BrowserRouter>

    </div>
  );
}

export default App;
